<?php

if (isset($_COOKIE["userIsLoggedIn"])) {
    header("Location: ./dashboard-pages/dashboard.php");
}

require_once("./dbconfig.php");
require_once("./UsersTable.php");
$errorMsg = "";

if ($_SERVER["REQUEST_METHOD"] == "POST") {
    $email = $_POST["email"];
    $password = $_POST["password"];

    if (filter_var($email, FILTER_VALIDATE_EMAIL)) {
        $usersTable = new UsersTable();
        $result = $usersTable->getUser($email);

        if ($result->rowCount()) {  //if user exists
            $user = $result->fetch();
            if (md5($password) == $user["password"]) {    //if password is valid
                setcookie("userIsLoggedIn", true);
                header("Location: ./dashboard-pages/dashboard.php");
            } else {
                $errorMsg = "Password is invalid";
            }
        } else {
            $errorMsg = "No user found with entered email";
        }
    } else {
        alert("Email invalid");
    }



    function alert($message)
    {
        echo "<script>alert('$message');</script>";
    }
}
